import { ColorRepresentation, IUniform } from 'three';
import { BlendMode, NoiseProps, NoiseType, MappingType } from '../types';
import Abstract from './Abstract';
export default class Noise extends Abstract {
    name: string;
    mode: BlendMode;
    type: NoiseType;
    mapping: MappingType;
    protected uuid: string;
    uniforms: {
        [key: string]: IUniform<any>;
    };
    constructor(props?: NoiseProps);
    private getMapping;
    private getNoise;
    getVertexVariables(): string;
    getVertexBody(e: string): string;
    getFragmentVariables(): string;
    getFragmentBody(e: string): string;
    set alpha(v: number);
    get alpha(): number;
    set colorA(v: ColorRepresentation);
    get colorA(): ColorRepresentation;
    set colorB(v: ColorRepresentation);
    get colorB(): ColorRepresentation;
    set scale(v: number);
    get scale(): number;
}
